const fs = require('fs');
const path = require('path');

const root = path.resolve(__dirname, '..');
const bundlePath = path.join(root, 'dist', 'webform-react.js');
const outPath = path.join(root, 'src', 'web', 'react', 'reactBundle.ts');

if (!fs.existsSync(bundlePath)) {
  console.error('[embed-react-bundle] Missing bundle at', bundlePath);
  process.exit(1);
}

const content = fs.readFileSync(bundlePath, 'utf8');
const literal = JSON.stringify(content);
const banner = `// Auto-generated by scripts/embed-react-bundle.js. Do not edit by hand.\n// Contains the React prototype bundle injected into the React template.\n`;
const output = `${banner}export const WEB_FORM_REACT_BUNDLE: string = ${literal};\n`;

fs.mkdirSync(path.dirname(outPath), { recursive: true });
fs.writeFileSync(outPath, output, 'utf8');
console.info('[embed-react-bundle] Wrote React bundle to', outPath);
