const fs = require('fs');
const path = require('path');

const root = path.resolve(__dirname, '..');
const bundlePath = path.join(root, 'dist', 'webform.js');
const outPath = path.join(root, 'src', 'web', 'webformBundle.ts');

if (!fs.existsSync(bundlePath)) {
  console.error('[embed-web-bundle] Missing bundle at', bundlePath);
  process.exit(1);
}

const content = fs.readFileSync(bundlePath, 'utf8');
const literal = JSON.stringify(content);
const banner = `// Auto-generated by scripts/embed-web-bundle.js. Do not edit by hand.\n// Contains the bundled client code injected into the web form template.\n`;
const output = `${banner}export const WEB_FORM_BUNDLE: string = ${literal};\n`;

fs.writeFileSync(outPath, output, 'utf8');
console.info('[embed-web-bundle] Wrote bundle to', outPath);
